import { useEffect, useMemo, useState } from 'react';
import HeroSection from './components/HeroSection';
import ProjectsSection from './components/ProjectsSection';
import ExperienceSection from './components/ExperienceSection';
import SkillsSection from './components/SkillsSection';
import ContactSection from './components/ContactSection';
import { getExperiences, getProfile, getProjects, getSkills } from './api';

const initialState = {
  profile: null,
  projects: [],
  experiences: [],
  skills: {},
};

function App() {
  const [data, setData] = useState(initialState);
  const [status, setStatus] = useState('loading'); // loading | ready | error
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    let active = true;

    async function fetchData() {
      try {
        const [profile, projects, experiences, skills] = await Promise.all([
          getProfile(),
          getProjects(),
          getExperiences(),
          getSkills(),
        ]);

        if (!active) {
          return;
        }

        setData({
          profile,
          projects,
          experiences,
          skills,
        });
        setStatus('ready');
      } catch (error) {
        if (!active) {
          return;
        }
        setErrorMessage(error.message);
        setStatus('error');
      }
    }

    fetchData();

    return () => {
      active = false;
    };
  }, []);

  const content = useMemo(() => {
    if (status === 'loading') {
      return (
        <div className="flex min-h-screen flex-col items-center justify-center bg-slate-900 text-white">
          <div className="h-12 w-12 animate-spin rounded-full border-4 border-sky-400 border-t-transparent" />
          <p className="mt-6 text-sm font-medium">Veriler yükleniyor…</p>
        </div>
      );
    }

    if (status === 'error' && !data.profile) {
      return (
        <div className="flex min-h-screen flex-col items-center justify-center bg-slate-900 text-white">
          <div className="rounded-3xl border border-white/10 bg-white/5 px-8 py-6 text-center shadow-xl">
            <h1 className="text-2xl font-semibold">Sunucuya bağlanılamadı</h1>
            <p className="mt-3 text-sm text-slate-200">
              Lütfen Spring Boot backend&apos;inin <code>http://localhost:8080</code> adresinde çalıştığından emin olun.
            </p>
            <p className="mt-4 text-xs text-slate-400">Hata: {errorMessage}</p>
          </div>
        </div>
      );
    }

    return (
      <div className="min-h-screen bg-slate-50 text-slate-900">
        {data.profile && <HeroSection profile={data.profile} />}
        <main>
          <ProjectsSection projects={data.projects} />
          <ExperienceSection experiences={data.experiences} />
          <SkillsSection skills={data.skills} />
          <ContactSection />
        </main>
        <footer className="mt-16 border-t border-slate-200 bg-white">
          <div className="mx-auto flex max-w-6xl flex-col items-center justify-between gap-4 px-4 py-8 text-sm text-slate-500 sm:flex-row sm:px-6 lg:px-8">
            <p>© {new Date().getFullYear()} Özge Solmaz. Tüm hakları saklıdır.</p>
            <nav className="flex gap-4">
              <a className="hover:text-slate-700" href="#projects">
                Projeler
              </a>
              <a className="hover:text-slate-700" href="#experience">
                Deneyim
              </a>
              <a className="hover:text-slate-700" href="#skills">
                Yetenekler
              </a>
              <a className="hover:text-slate-700" href="#contact">
                İletişim
              </a>
            </nav>
          </div>
        </footer>
      </div>
    );
  }, [data, errorMessage, status]);

  return content;
}

export default App;
