import Section from './Section';

function groupEntries(skills) {
  return Object.entries(skills ?? {});
}

export default function SkillsSection({ skills }) {
  const grouped = groupEntries(skills);

  return (
    <Section id="skills" eyebrow="Yetenekler" title="Teknik yetkinliklerim">
      <div className="grid gap-8 md:grid-cols-2">
        {grouped.map(([category, skillList]) => (
          <div key={category} className="rounded-2xl border border-slate-200 bg-white p-6 shadow-sm">
            <h3 className="text-lg font-semibold text-slate-900">{category}</h3>
            <ul className="mt-4 space-y-4">
              {skillList.map((skill) => (
                <li key={skill.name} className="space-y-2">
                  <div className="flex items-center justify-between text-sm font-medium text-slate-600">
                    <span>{skill.name}</span>
                    <span>{skill.proficiency}%</span>
                  </div>
                  <div className="h-2 rounded-full bg-slate-200">
                    <div
                      className="h-2 rounded-full bg-gradient-to-r from-sky-500 to-indigo-500"
                      style={{ width: `${skill.proficiency}%` }}
                    />
                  </div>
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    </Section>
  );
}
