import { useState } from 'react';
import Section from './Section';
import { submitContact } from '../api';

const initialForm = {
  name: '',
  email: '',
  message: '',
};

export default function ContactSection() {
  const [form, setForm] = useState(initialForm);
  const [status, setStatus] = useState('idle'); // idle | sending | success | error
  const [errorMessage, setErrorMessage] = useState('');

  const handleChange = (event) => {
    const { name, value } = event.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setStatus('sending');
    setErrorMessage('');

    try {
      await submitContact(form);
      setStatus('success');
      setForm(initialForm);
    } catch (error) {
      setStatus('error');
      setErrorMessage(error.message);
    }
  };

  return (
    <Section id="contact" eyebrow="İletişim" title="Birlikte çalışalım">
      <div className="grid gap-10 lg:grid-cols-2">
        <div className="rounded-3xl border border-slate-200 bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900 p-10 text-slate-100 shadow-xl">
          <h3 className="text-2xl font-semibold">Yeni projelere açığım</h3>
          <p className="mt-4 text-sm leading-6 text-slate-300">
            Ürününüze yeni özellikler kazandırmak, yapay zeka tabanlı çözümler tasarlamak
            ya da mevcut kod tabanınızı güçlendirmek için desteğe ihtiyacınız varsa
            bir kahve eşliğinde konuşalım.
          </p>
          <div className="mt-8 space-y-3 text-sm">
            <p>
              <span className="font-semibold text-white">Email: </span>
              <a className="text-sky-300 hover:text-sky-100" href="mailto:ozge@example.com">
                ozge@example.com
              </a>
            </p>
            <p>
              <span className="font-semibold text-white">Lokasyon: </span>
              İstanbul, Türkiye (Uzak çalışma uygun)
            </p>
          </div>
        </div>

        <form
          onSubmit={handleSubmit}
          className="rounded-3xl border border-slate-200 bg-white p-8 shadow-lg"
        >
          <div className="grid gap-6 sm:grid-cols-2">
            <label className="text-sm font-medium text-slate-700">
              İsim
              <input
                type="text"
                name="name"
                value={form.name}
                onChange={handleChange}
                required
                maxLength={100}
                className="mt-2 w-full rounded-xl border border-slate-300 px-4 py-3 text-sm shadow-sm focus:border-sky-500 focus:outline-none focus:ring-2 focus:ring-sky-200"
                placeholder="Adınız"
              />
            </label>
            <label className="text-sm font-medium text-slate-700">
              Email
              <input
                type="email"
                name="email"
                value={form.email}
                onChange={handleChange}
                required
                className="mt-2 w-full rounded-xl border border-slate-300 px-4 py-3 text-sm shadow-sm focus:border-sky-500 focus:outline-none focus:ring-2 focus:ring-sky-200"
                placeholder="you@example.com"
              />
            </label>
          </div>
          <label className="mt-6 block text-sm font-medium text-slate-700">
            Mesaj
            <textarea
              name="message"
              value={form.message}
              onChange={handleChange}
              required
              maxLength={2000}
              rows={6}
              className="mt-2 w-full rounded-xl border border-slate-300 px-4 py-3 text-sm shadow-sm focus:border-sky-500 focus:outline-none focus:ring-2 focus:ring-sky-200"
              placeholder="Projenizi anlatabilirsiniz…"
            />
          </label>
          <button
            type="submit"
            disabled={status === 'sending'}
            className="mt-6 w-full rounded-full bg-sky-600 px-6 py-3 text-sm font-semibold text-white shadow hover:bg-sky-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-sky-600 disabled:cursor-not-allowed disabled:bg-slate-400"
          >
            {status === 'sending' ? 'Gönderiliyor…' : 'Mesajı gönder'}
          </button>

          {status === 'success' && (
            <p className="mt-4 text-sm font-medium text-emerald-600">
              Teşekkürler! Kısa süre içinde dönüş yapacağım.
            </p>
          )}
          {status === 'error' && (
            <p className="mt-4 text-sm font-medium text-rose-600">
              Bir sorun oluştu: {errorMessage}
            </p>
          )}
        </form>
      </div>
    </Section>
  );
}
