import Section from './Section';

export default function ProjectsSection({ projects }) {
  return (
    <Section
      id="projects"
      eyebrow="Projeler"
      title="Seçtiğim işler ve topluluk çalışmaları"
    >
      <div className="grid gap-8 md:grid-cols-2">
        {projects.map((project) => (
          <article
            key={project.id}
            className="flex h-full flex-col justify-between rounded-2xl border border-slate-200 bg-white p-6 shadow-sm transition hover:-translate-y-1 hover:shadow-xl"
          >
            <div>
              <div className="text-xs font-semibold uppercase tracking-wide text-sky-500">
                {project.technologies.join(' • ')}
              </div>
              <h3 className="mt-3 text-2xl font-semibold text-slate-900">{project.name}</h3>
              <p className="mt-4 text-sm leading-6 text-slate-600">{project.description}</p>
            </div>
            <div className="mt-6 flex flex-wrap items-center gap-4 text-sm font-medium text-sky-600">
              {project.liveUrl && (
                <a href={project.liveUrl} target="_blank" rel="noreferrer" className="hover:text-sky-800">
                  Canlı Demo →
                </a>
              )}
              {project.sourceUrl && (
                <a href={project.sourceUrl} target="_blank" rel="noreferrer" className="hover:text-sky-800">
                  Kaynak Kodu →
                </a>
              )}
              {!project.liveUrl && !project.sourceUrl && (
                <span className="text-slate-400">Linkler yakında eklenecek</span>
              )}
            </div>
          </article>
        ))}
      </div>
    </Section>
  );
}
